import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.net.URL;

public class GeneCounter {

    public static void main(String[] args) throws IOException {
        String dna = "";

        int geneCount = countGenes(dna);
        System.out.println("Gene count: " + geneCount);

        int longGeneCount = countLongGenes(dna, 60);
        System.out.println("Gene count with length > 60: " + longGeneCount);

        int cgRatioGeneCount = countCgRatioGenes(dna, 0.35);
        System.out.println("Gene count with CG ratio > 0.35: " + cgRatioGeneCount);

        int ctgCount = countCodon(dna, "CTG");
        System.out.println("Number of times CTG appears: " + ctgCount);

    }

    private static String readDNAFromURL(String url) throws IOException {
        URL dataURL = new URL(url);
        BufferedReader reader = new BufferedReader(new InputStreamReader(dataURL.openStream()));

        StringBuilder dnaBuilder = new StringBuilder();
        String line;
        while ((line = reader.readLine()) != null) {
            if (line.startsWith(">")) {
                continue;
            }
            dnaBuilder.append(line);
        }
        return dnaBuilder.toString();
    }

    private static int countGenes(String dnaSequence) {
        int geneCount = 0;
        int startIndex = dnaSequence.indexOf("ATG");
        while (startIndex >= 0) {
            int endIndex = findEndCodonIndex(dnaSequence, startIndex);
            if (endIndex >= 0) {
                String gene = dnaSequence.substring(startIndex, endIndex + 3);
                geneCount++;
                startIndex = dnaSequence.indexOf("ATG", endIndex);
            } else {
                startIndex = dnaSequence.indexOf("ATG", startIndex + 1);
            }
        }
        return geneCount;
    }

    private static int countLongGenes(String dnaSequence, int length) {
        int longGeneCount = 0;
        int startIndex = dnaSequence.indexOf("ATG");
        while (startIndex >= 0) {
            int endIndex = findEndCodonIndex(dnaSequence, startIndex);
            if (endIndex >= 0) {
                String gene = dnaSequence.substring(startIndex, endIndex + 3);
                if (gene.length() > length) {
                    longGeneCount++;
                }
                startIndex = dnaSequence.indexOf("ATG", endIndex);
            } else {
                startIndex = dnaSequence.indexOf("ATG", startIndex + 1);
            }
        }
        return longGeneCount;
    }

    private static int countCgRatioGenes(String dnaSequence, double cgRatio) {
        int cgRatioGeneCount = 0;
        int startIndex = dnaSequence.indexOf("ATG");
        while (startIndex >= 0) {
            int endIndex = findEndCodonIndex(dnaSequence, startIndex);
            if (endIndex >= 0) {
                String gene = dnaSequence.substring(startIndex, endIndex + 3);
                double geneCgRatio = calculateCGRatio(gene);
                if (geneCgRatio > cgRatio) {
                    cgRatioGeneCount++;
                }
                startIndex = dnaSequence.indexOf("ATG", endIndex + 3);
            } else {
                startIndex = dnaSequence.indexOf("ATG", startIndex + 3);
            }
        }
        return cgRatioGeneCount;
    }

    private static int countCodon(String dnaSequence, String codon) {
        int count = 0;
        int startIndex = dnaSequence.indexOf(codon);
        while (startIndex >= 0) {
            count++;
            startIndex = dnaSequence.indexOf(codon, startIndex + 1);
        }
        return count;
    }

    private static int findEndCodonIndex(String dnaSequence, int startIndex) {
        for (int i = startIndex + 3; i < dnaSequence.length() - 2; i += 3) {
            String triplet = dnaSequence.substring(i, i + 3);
            if (triplet.equals("TAA") || triplet.equals("TAG") || triplet.equals("TGA")) {
                return i;
            }
        }
        return -1;
    }

    private static double calculateCGRatio(String dnaSequence) {
        int cCount = countCodon(dnaSequence, "C");
        int gCount = countCodon(dnaSequence, "G");
        return (double) (cCount + gCount) / dnaSequence.length();
    }

    private static String readDNASequence(String url) throws IOException {
        URL dnaUrl = new URL(url);
        BufferedReader in = new BufferedReader(new InputStreamReader(dnaUrl.openStream()));

        StringBuilder dnaBuilder = new StringBuilder();
        String line;
        while ((line = in.readLine()) != null) {
            if (!line.startsWith(">")) {
                dnaBuilder.append(line);
            }
        }
        in.close();

        return dnaBuilder.toString();
    }
    
}

