        String dna1 = "ATGCGGTAAGCGAGGGTGACAGCATGTGGAGCT";
        String dna2 = "ATGTGACTGAACTGACTGACGAT";
        String dna3 = "ATGCGGTAAGCGAGGGTGGTGCGCGCGCGCGCGCGCGCGCGCGCGCGCGCGCGCGCGAGCT";
        String dna4 = "ATGCGGTAAGCGAGGGTGACAGCATGTGGAGCTATCGGTTGACG";
        String dna5 = "ATGCGGTAAGCGAGGGTGACAGCATGTGGAGCTCGCGCGCGCGCTG";
