package D04_dnaAnalyser;

public class GeneCounter4 {

    public static void main(String[] args) {
        String dna = ""; // replace with your DNA sequence
        int geneCount = countGenes(dna);
        System.out.println("Number of genes: " + geneCount);
    }

    private static int countGenes(String sequence) {
        int geneCount = 0;
        int start = 0;
        while (start < sequence.length()) {
            int startIndex = sequence.indexOf("ATG", start);
            if (startIndex == -1) {
                break;
            }
            int stopIndex = findStopCodon(sequence, startIndex);
            if (stopIndex == -1) {
                break;
            }
            geneCount++;
            start = stopIndex + 3;
        }
        return geneCount;
    }

    private static int findStopCodon(String sequence, int startIndex) {
        int stop1 = sequence.indexOf("TAA", startIndex + 3);
        if (stop1 == -1 || (stop1 - startIndex) % 3 != 0) {
            stop1 = sequence.length();
        }
        int stop2 = sequence.indexOf("TAG", startIndex + 3);
        if (stop2 == -1 || (stop2 - startIndex) % 3 != 0) {
            stop2 = sequence.length();
        }
        int stop3 = sequence.indexOf("TGA", startIndex + 3);
        if (stop3 == -1 || (stop3 - startIndex) % 3 != 0) {
            stop3 = sequence.length();
        }
        return Math.min(stop1, Math.min(stop2, stop3));
    }
}
